<?php    $pageTitle = 'Login';    if (isset($_SESSION['userID'])) {        header('Location: index.php');    }    include 'init.php';    include 'Boxes/forget-password.php';    if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['login'])) {        $id = $_POST['id'];        $password = $_POST['password'];        $branch = $_POST['branch'];        $stmt = $connect->prepare("SELECT staff.job_title_id FROM staff JOIN sys_users on staff.staff_id = sys_users.staff_id WHERE sys_users.user_id ='$id'");        $stmt->execute();        $row = $stmt->fetch();        if ($row['job_title_id'] == '1' || $row['job_title_id'] == '2') {            $stmt = $connect->prepare("SELECT user_id, password FROM sys_users WHERE user_id = '$id' AND password = '$password'");        } else {            $stmt = $connect->prepare("SELECT user_id, password, branch_id FROM sys_users, branch_users WHERE user_id = '$id' AND password = '$password' AND branch_id = '$branch'");        }        $stmt->execute();        $row = $stmt->fetch();        $check = $stmt-> rowCount();        if ($check > 0) {            $_SESSION['userID'] = $row['user_id'];            header("Location: index.php");        } else {            $notify = '<div class="alert alert-danger custom-alert">Invalid <strong>Password</strong>.</div>';        }    }?><div class="login text-center">    <div class="container-fluid">        <div class="page-header">            <h1>Back-Packers Information System</h1>        </div>        <div class="row">            <div class="logoImage text-center col-sm-4 col-md-6 hidden-xs">                <img class="img-responsive" src="layout/images/logo-black.png">            </div>            <div class="col-sm-8 col-md-6">                <form class="form-horizontal" action="<?php $_SERVER['PHP_SELF']; ?>" method="POST">                    <div class="form-group">                        <label for="id" class="col-xs-1 control-label">ID</label>                        <div class="col-sm-10 col-sm-push-1 col-xs-9 col-xs-push-2">                            <input type="text" class="form-control" id="id" placeholder="ID" name="id">                        </div>                    </div>                    <div class="form-group">                        <label for="password" class="col-xs-1 control-label">Password</label>                        <div class="col-sm-10  col-sm-push-1 col-xs-9 col-xs-push-2">                            <input type="password" class="form-control" id="password" placeholder="Password" name="password">                        </div>                    </div>                    <div class="form-group">                        <label for="branch" class="col-xs-1 control-label">Branch</label>                        <div class="col-sm-10 col-sm-push-1 col-xs-9 col-xs-push-2">                            <select class="form-control" id="branch" name="branch">                                <option value="" disabled selected>Select your branch</option>                                <?php                                    $stmt = $connect->prepare("SELECT branch_id, branch_name FROM branches");                                    $stmt->execute();                                    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {                                        echo '<option value="'.$row['branch_id'].'">'.$row['branch_name'].'</option>';                                    }                                ?>                            </select>                        </div>                    </div>                    <div class="form-group">                        <div class="col-sm-5 col-xs-6">                            <a href="<?php echo $_SERVER['PHP_SELF'].'?id=1'; ?>" type="button" class="btn btn-warning btn-block forget-button">Forget Password</a>                        </div>                        <div class="col-sm-7 col-xs-6">                            <button type="submit" class="btn btn-success btn-block" name="login">Login</button>                        </div>                    </div>                    <?php                        if(isset($notify)) {                            echo $notify;                        }                    ?>                </form>            </div>        </div>    </div></div><?php    include $templates . 'footer.php';?>